int f[maxn];string S[4];int ord[3]={0,1,2};int ans=maxlongint;//z-algorithminline string skr(string a,string b){	string T=a+'$'+b;//merge a &amp; b	int n=a.length();	int m=b.length();	int Len=n+m+1;	f[0]=Len;	int j=1,k;	for (int i=1;i&lt;Len;i=k)	{		if (j&lt;i) j=i;		while (T[j-i]==T[j] &amp;&amp; j&lt;Len)			j++;		f[i]=j-i;		k=i+1;		while (k+f[k-i]&lt;j) k++;	}	int Lim=m+1;	for (int i=n+1;i&lt;Len;i++)	{		if(f[i]==n)	return b;		if (i+f[i]&gt;=Len){			Lim=min(Lim,i-n);break;		}	}	string cur;	cur=b.substr(0,Lim-1)+a;	return cur;}//mainint main(){	cin&gt;&gt;S[0]&gt;&gt;S[1]&gt;&gt;S[2];	do {		ans=min(ans,(int)skr(skr(S[ord[0]],S[ord[1]]),S[ord[2]]).length());	}while (next_permutation(ord,ord+3));	//æš´åŠ›æšä¸¾ä¸‰ä¸ªä¸²é“¾æ¥çš„é¡ºåº	cout&lt;&lt;ans&lt;&lt;endl;	return 0;} 

